mapper.select:
select 

from ON_BUSIN_CHANCE
where PROVIDERID = #{providerid,jdbcType=DECIMAL} and BC_ID = #{bcid,jdbcType=DECIMAL}

select count(1) from (SELECT a.bc_id FROM busin_chance a where a.providerid = #{providerId}
    and a.states = #{state}
     and a.supcatid = #{supcatid}
     and a.showstate = #{showstate}
     and a.sorttag = #{sorttag}
     and a.serviceflag = '1'
     and a.serviceflag is null
UNION
SELECT b.bc_id FROM on_busin_chance b where b.providerid = #{providerId}
     and b.states = #{state}
     and b.supcatid = #{supcatid}
     and b.showstate = #{showstate}
     and b.sorttag = #{sorttag}
     and b.serviceflag = '1'
     and b.serviceflag is null
)

select count(*) from (SELECT a.bc_id FROM busin_chance a where a.providerid = #{providerId} and a.states='0'and a.seriesid = #{seriesid}
  UNION
SELECT b.bc_id FROM on_busin_chance b where b.providerid = #{providerId} and b.states='0' and b.seriesid = #{seriesid})
